import React, { useState, useEffect } from 'react';
import './App.css';


function App() {
  const [state, setState] = useState({
    textField: ''
  });

  return (
    <div className="App">
      <header className="App-header">          
        <input 
          name="textField"
          onChange={(e) => setState({ ...state, textField: e.target.value })}
          value={state.textField}
        />
        <p>{text}</p>
      </header>
    </div>
  );
}

export default App;
