import React from 'react';
import { Component } from 'react';
import './App.css';


class App extends Component{
  state = {
    text: ""
  }
  handleChange = (e) => {
    this.setState({
      text: e.target.value
    })  
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">          
          <input name=""
             onChange={ this.handleChange }   
            value={ this.state.text }
          />

          <p>
           Algo {this.state.text}
          </p>
        </header>
      </div>
    );
  }
}

export default App;
